// Extract DGN from CAN ID
const canId = msg.payload.id;
const dgn = (canId >> 8) & 0x1FFFF;

// Only process thermostat/AC related messages
const THERMOSTAT_STATUS_1 = 0x1FED9; // Thermostat Status 1
const THERMOSTAT_STATUS_2 = 0x1FEDA; // Thermostat Status 2
const AC_STATUS = 0x1FF85;  // Air Conditioner Status

let payload = null;

if (dgn === THERMOSTAT_STATUS_1) {
    // Process Thermostat Status 1 message
    const data = msg.payload.data;
    const instance = data[0]; // Instance byte

    // Decode operating mode (bits 0-1 of byte 1)
    const modeCode = data[1] & 0x03;
    let operatingMode = "unknown";
    let operatingModeDefinition = "unknown";

    switch(modeCode) {
        case 0: operatingMode = "0000"; operatingModeDefinition = "off"; break;
        case 1: operatingMode = "0001"; operatingModeDefinition = "cool"; break;
        case 2: operatingMode = "0010"; operatingModeDefinition = "heat"; break;
        case 3: operatingMode = "0011"; operatingModeDefinition = "auto"; break;
    }

    // Decode fan mode (bits 2-3 of byte 1)
    const fanModeCode = (data[1] >> 2) & 0x03;
    let fanMode = "unknown";
    let fanModeDefinition = "unknown";

    switch(fanModeCode) {
        case 0: fanMode = "00"; fanModeDefinition = "auto"; break;
        case 1: fanMode = "01"; fanModeDefinition = "low"; break;
        case 2: fanMode = "10"; fanModeDefinition = "medium"; break;
        case 3: fanMode = "11"; fanModeDefinition = "high"; break;
    }

    // Temperature setpoints come from bytes 2-5
    // Ambient temperature comes from byte 6-7

    // Convert byte pairs to temperature in Celsius
    const coolSetpoint = ((data[2] | (data[3] << 8)) - 273) / 10;
    const heatSetpoint = ((data[4] | (data[5] << 8)) - 273) / 10;
    const ambientTemp = ((data[6] | (data[7] << 8)) - 273) / 10;

    // Convert to Fahrenheit
    const coolSetpointF = (coolSetpoint * 9/5) + 32;
    const heatSetpointF = (heatSetpoint * 9/5) + 32;
    const ambientTempF = (ambientTemp * 9/5) + 32;

    payload = {
        dgn: dgn.toString(16).toUpperCase(),
        name: "THERMOSTAT_STATUS_1",
        instance: instance,
        "operating mode": operatingMode,
        "operating mode definition": operatingModeDefinition,
        "fan mode": fanMode,
        "fan mode definition": fanModeDefinition,
        "setpoint temp cool": coolSetpoint,
        "setpoint temp cool F": coolSetpointF,
        "setpoint temp heat": heatSetpoint,
        "setpoint temp heat F": heatSetpointF,
        "ambient temp": ambientTemp,
        "ambient temp F": ambientTempF,
        data: Buffer.from(data).toString('hex').toUpperCase(),
        timestamp: new Date().getTime() / 1000
    };

} else if (dgn === THERMOSTAT_STATUS_2) {
    // Process Thermostat Status 2 message
    const data = msg.payload.data;
    const instance = data[0]; // Instance byte

    // Get schedule mode from byte 1
    const scheduleMode = data[1] & 0x03;
    let scheduleModeDefinition = "unknown";

    switch(scheduleMode) {
        case 0: scheduleModeDefinition = "disabled"; break;
        case 1: scheduleModeDefinition = "weekday/weekend"; break;
        case 2: scheduleModeDefinition = "seven day"; break;
        case 3: scheduleModeDefinition = "reserved"; break;
    }

    payload = {
        dgn: dgn.toString(16).toUpperCase(),
        name: "THERMOSTAT_STATUS_2",
        instance: instance,
        "schedule mode": scheduleMode.toString(2).padStart(2, '0'),
        "schedule mode definition": scheduleModeDefinition,
        data: Buffer.from(data).toString('hex').toUpperCase(),
        timestamp: new Date().getTime() / 1000
    };

} else if (dgn === AC_STATUS) {
    // Process AC Status message
    const data = msg.payload.data;
    const instance = data[0]; // Instance byte

    // Get AC operating mode from byte 1
    const acModeCode = data[1] & 0x0F;
    let acOperatingMode = "unknown";

    switch(acModeCode) {
        case 0: acOperatingMode = "off"; break;
        case 1: acOperatingMode = "cool"; break;
        case 2: acOperatingMode = "heat"; break;
        case 3: acOperatingMode = "auto"; break;
        case 4: acOperatingMode = "fan only"; break;
        case 5: acOperatingMode = "dry"; break;
    }

    // Get fan speed from byte 2
    const fanSpeed = data[2];

    payload = {
        dgn: dgn.toString(16).toUpperCase(),
        name: "AC_STATUS",
        instance: instance,
        "operating mode": acModeCode.toString(2).padStart(4, '0'),
        "operating mode definition": acOperatingMode,
        "fan speed": fanSpeed,
        data: Buffer.from(data).toString('hex').toUpperCase(),
        timestamp: new Date().getTime() / 1000
    };
}

// Only pass on relevant messages
if (payload) {
    msg.payload = payload;
    msg.topic = `RVC/${payload.name}/${payload.instance}`;
    return msg;
}

return null; // Drop non-relevant messages

// Function to generate CAN messages for thermostat control
// This is used when a command is received from MQTT

// Get the specific command from the MQTT topic
const topicParts = msg.topic.split('/');
if (topicParts.length < 3 || topicParts[0] !== 'RVC' || !topicParts[2]) {
    // Invalid topic format
    return null;
}

const commandType = topicParts[1]; // e.g., THERMOSTAT_COMMAND_1
const instance = parseInt(topicParts[2]) || 0;

let canMsg = null;

if (commandType === 'THERMOSTAT_COMMAND_1') {
    // Format a CAN message for thermostat command
    const payload = msg.payload;

    // Create data buffer for CAN message
    const data = Buffer.alloc(8, 0xFF);
    data[0] = instance; // Set instance

    // Set operating mode in byte 1 (bits 0-1)
    if (payload['operating mode']) {
        const modeMap = {
            '0000': 0, // off
            '0001': 1, // cool
            '0010': 2, // heat
            '0011': 3  // auto
        };
        const modeBits = modeMap[payload['operating mode']] || 0;
        data[1] = (data[1] & 0xFC) | modeBits; // Keep upper 6 bits, set lower 2
    }

    // Set fan mode in byte 1 (bits 2-3)
    if (payload['fan mode']) {
        const fanMap = {
            '00': 0, // auto
            '01': 1, // low
            '10': 2, // medium
            '11': 3  // high
        };
        const fanBits = fanMap[payload['fan mode']] || 0;
        data[1] = (data[1] & 0xF3) | (fanBits << 2); // Keep bits 0-1 and 4-7, set bits 2-3
    }

    // Set cool setpoint in bytes 2-3 if provided
    if (payload['setpoint temp cool'] !== undefined) {
        // Convert Celsius to RV-C format (0.1K steps, with 0K = -273.15Â°C)
        const tempVal = Math.round((payload['setpoint temp cool'] + 273.15) * 10);
        data[2] = tempVal & 0xFF;
        data[3] = (tempVal >> 8) & 0xFF;
    }

    // Set heat setpoint in bytes 4-5 if provided
    if (payload['setpoint temp heat'] !== undefined) {
        // Convert Celsius to RV-C format
        const tempVal = Math.round((payload['setpoint temp heat'] + 273.15) * 10);
        data[4] = tempVal & 0xFF;
        data[5] = (tempVal >> 8) & 0xFF;
    }

    // Construct CAN ID for THERMOSTAT_COMMAND_1 (DGN 0x1FEC9)
    const canId = 0x1FEC9 << 8;

    canMsg = {
        id: canId,
        data: data,
        ext: true // Extended frame format
    };
}

if (canMsg) {
    msg.payload = canMsg;
    return msg;
}

return null;
